// Helper: get lang code and strings; for unsupported langs, return en + instruction
export function getOppBlindSpotsStrings(langCode: string): { s: OppBlindSpotsStrings; useInstruction: boolean } {
  const code = (langCode || "en").toLowerCase().split("-")[0] as VideoLangCode;
  const s = OPP_BLIND_SPOTS[code] || OPP_BLIND_SPOTS.en;
  return { s, useInstruction: !OPP_BLIND_SPOTS[code] };
}

