// For reports without full translation maps yet, use instruction-based approach
export function getLangInstruction(langCode: string): string {
  const code = (langCode || "en").toLowerCase().split("-")[0];
  if (code === "en") return "";
  const langName = LANGUAGE_NAMES[code] || LANGUAGE_NAMES[langCode] || "English";
  return `OUTPUT LANGUAGE: ${langName}. MANDATORY: Generate ALL on-screen text, titles, labels, numbers, charts, and written content in ${langName} only. Translate every English phrase in this prompt to ${langName} for display. No English text on screen.\n\n`;
}
